  export let question: string = BREAKERS[0]
  export let color: string = 'black'

  let breakerIndex = -1
  let runningTimeout = null
  const getContainer = () => document.querySelector('h1')

  const retypeContent = (from, to, wasReducing = true) => {
    if (question === to) {
      clearTimeout(runningTimeout)
      return
    }
    const isReducing = !to.startsWith(question)
    let delay
    if (isReducing) {
      question = from.slice(0, from.length - 20 / from.length /* dummy exp increase */)
      delay = Math.random() * 10
    } else {
      if (wasReducing) {
        color = COLORS[breakerIndex % COLORS.length]
      }
      const delta = to.slice(from.length, from.length + 1)
      question = from + delta
      delay =
        CHARACTER_TYPING_DELAY +
        Math.random() * CHARACTER_TYPING_JITTER * (delta === ' ' ? END_WORD_TYPING_MULTIPLIER : 1) +
        (delta === '?' ? QUESTION_TYPING_DELAY : 0)
    }
    clearTimeout(runningTimeout)
    runningTimeout = setTimeout(() => retypeContent(question, to, isReducing), delay)
  }
  const synthesizeSpeech = text => {
    window.speechSynthesis.cancel()
    var msg = new SpeechSynthesisUtterance()
    var voices = window.speechSynthesis.getVoices()
    const voiceScore = x => (x.lang.match('en') ? 2 : 0) + Number(x.default)
    msg.voice = voices.sort((a, b) => voiceScore(b) - voiceScore(a))[0]
    msg.volume = 1
    msg.rate = 1
    msg.pitch = 0.8
    msg.text = text
    msg.lang = 'en'
    window.speechSynthesis.speak(msg)
  }
  const changeQuestion = async text => {
    clearTimeout(runningTimeout)
    retypeContent(question, text)
  }
  const slideQuestion = (delta = 1) => {
    if (breakerIndex === -1) {
      question = ''
    }
    const prev = breakerIndex
    breakerIndex = Math.min(Math.max(breakerIndex + delta, 0), BREAKERS.length - 1)
    if (prev === breakerIndex) return
    changeQuestion(BREAKERS[breakerIndex])
    synthesizeSpeech(BREAKERS[breakerIndex])
  }
